import './App.css';
import { useState } from 'react';

function App() {

  const [params, setParams] = useState({ pris: 0, god: 1, volym: 2 });

  const handleChange = (evt) => {
    const value = evt.target.value;
    setParams({
      ...params, [evt.target.name]: value
    });
  }


  return (
    <div className="App">
      <p>Hur mycket värd är din öl?</p>
      <div className='params'>
        <div>
          pris <input type="text" name="pris" value={params.pris} onChange={handleChange} />
        </div>
        <div>
          god <input type="text" name="god" value={params.god} onChange={handleChange} />
        </div>
        <div>
          volym <input type="text" name="volym" value={params.volym} onChange={handleChange} /> cl
        </div>
      </div>
      njutningsvärd: {params.pris / params.god}
    </div>
  );
}

export default App;
