import './App.css';
import { useState } from 'react';

function App() {

  const [params, setParams] = useState({ pris: 0, god: 1, volym: 2, njutningsasymptot: 1 });

  const handleChange = (evt) => {
    const value = evt.target.value;
    setParams({
      ...params, [evt.target.name]: value
    });
  }

  console.log(params)


